package dataConversioninJava;

public class Numeric_anytype_toStringtype {
//String class provides static overloaded valueOf() method to convert any basic type of data into String type.
	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		int a= 12;
		String str= String.valueOf(a);
		System.out.println(str);
		
		
		float f= 22.4f;
		String str1= String.valueOf(f);
		System.out.println(str1);
		
		
		
		
		
	}

}
