package loops;

import java.util.Scanner;

public class ForLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		Scanner sc=new Scanner (System.in);
		System.out.println("Enter your number");
		
		int n=sc.nextInt();
		
		for (int i=1;i<n;i++)
		{
			System.out.println(i);
			
		}
		
		
		
	}

}
